@@ lib/<: $dist.path :>.pm
package <: $dist.module :>;
use utf8;
use strict;
use warnings;
use v5.14;

our $VERSION = "0.01";

use Carp ();
use Module::Load ();
use Scalar::Util qw(blessed);
use Scope::Container;
use Try::Tiny;

use <: $dist.module :>::Context;
use <: $dist.module :>::Error;
use <: $dist.module :>::Logger;

sub to_app {
    my $class = shift;
    return sub {
        my $env = shift;
        return $class->handle_request($env);
    };
}

sub handle_request {
    my ($class, $env) = @_;

    my $context = <: $dist.module :>::Context->from_env($env);
    scope_container context => $context if in_scope_container;

    my $route = $context->route;

    my ($engine, $action) = split /\s+/, $route->{action};

    try {
        $class->before_dispatch($context);

        unless ($engine->can($action)) {
            $context->error(404 => "Not Found action. $action");
        }
        Module::Load::load $engine;

        $engine->$action($context);

        $class->after_dispatch($context);
    } catch {
        my $e = $_;

        if (blessed $e && $e->isa('<: $dist.module :>::Error')) {
            my $res = $context->response;
            $res->code($e->{code});
            $res->header('X-Error-Message' => $e->{message}) if $e->{message};
            $res->content_type('text/plain');

            if (defined $e->{location}) {
                $res->header('Location' => $e->{location});
            }

            unless (defined $res->content) {
                $res->content($e->{message});

                eval {
                    my $name = $e->{html} || $e->{code} . '.html';
                    $context->html($name,
                        message => $e->{message},
                        %{ $e->{stash} || {} }
                    );
                };
                if ($@) {
                    WARN $@;
                }
            }

            $res->headers->header(X_Dispatch => $engine);

            return $res->finalize;
        }
        else {
            CRIT "%s", $e;
            die $e;
        }
    };

    my $res = $context->response;

    $res->headers->header(X_Dispatch => $engine);

    return $res->finalize;
}

sub before_dispatch {
    my ($class, $c) = @_;
}

sub after_dispatch {
    my ($class, $c) = @_;
}

1;
__END__

