@@ lib/<: $dist.path :>/Context.pm
package <: $dist.module :>::Context;
use utf8;
use strict;
use warnings;
use v5.14;

use Plack::Session;

use <: $dist.module :>::Config;
use <: $dist.module :>::DBManager;
use <: $dist.module :>::Request;
use <: $dist.module :>::Views;

use Class::Accessor::Lite::Lazy (
    new => 1,
    ro => [ 'env' ],
    ro_lazy => [qw(
        request
        response
        route
        db
        session
    )],
    rw_lazy => [ qw() ],
);

*req = \&request;
*res = \&response;

sub from_env {
    my ($class, $env) = @_;
    return $class->new(env => $env);
}

sub _build_request {
    my $self = shift;
    return <: $dist.module :>::Request->new($self->env);
}

sub _build_response {
    my $self = shift;
    return $self->request->new_response(200);
}

sub _build_route {
    my $self = shift;
    return <: $dist.module :>::Config->router->match($self->env);
}

sub _build_session {
    my $self = shift;
    return Plack::Session->new($self->env);
}

sub _build_db {
    return <: $dist.module :>::DBManager->new;
}

sub dbh {
    my $self = shift;
    return $self->db->dbh(@_);
}

sub error {
    my ($self, $code, $message, %opts) = @_;
    <: $dist.module :>::Error->throw(code => $code, message => $message, %opts);
}

sub redirect {
    my ($self, $location, %opts) = @_;
    <: $dist.module :>::Error->throw(code => 302, location => $location, %opts);
}

1;

@@ lib/<: $dist.path :>/DBI.pm
package <: $dist.module :>::DBI;
use utf8;
use strict;
use warnings;
use parent qw(Scope::Container::DBI);
use Carp qw(croak);

use <: $dist.module :>::Config;

sub connect {
    my ($class, $name) = @_;

    my $dbconfig = <: $dist.module :>::Config->param("db.$name")
        or croak "Not found db config: $name";
    my $dsn  = $dbconfig->{dsn}    or croak 'Not found dsn';
    my $user = $dbconfig->{user}   or croak 'Not found user';
    my $pass = $dbconfig->{passwd} or croak 'Not found passwd';

    my $dbh = $class->SUPER::connect($dsn, $user, $pass,
        {
            RootClass           => 'DBIx::Sunny',
            RaiseError          => 1,
            PrintError          => 0,
            ShowErrorStatement  => 1,
            AutoInactiveDestroy => 1,
            mysql_enable_utf8   => 1,
        }
    );
    return $dbh;
}

1;

@@ lib/<: $dist.path :>/DBManager.pm
package <: $dist.module :>::DBManager;
use utf8;
use strict;
use warnings;

use Carp;
use UNIVERSAL::require;
use Scope::Container::DBI;
use Class::Accessor::Lite::Lazy new => 1;

use <: $dist.module :>::DBI;
use <: $dist.module :>::Util;

sub dbh {
    my ($self, $name) = @_;
    my $dbh = <: $dist.module :>::DBI->connect($name);
    $dbh;
}

sub single {
    my ($self, %opts) = @_;

    ($opts{sql}, $opts{bind}) = <: $dist.module :>::Util::bind_named($opts{sql}, $opts{bind} || {});

    $self->single_nonamed(%opts);
}

sub single_nonamed {
    my ($self, %opts) = @_;
    $opts{class}->require or die $@ if $opts{class};

    my $sql = $opts{sql};
    my $bind = $opts{bind};

    my $res = $self->dbh($opts{db})->selectrow_hashref($sql, {}, @$bind);
    $res or return '';

    if ($opts{class}) {
        $opts{class}->new($res);
    } else {
        $res;
    }
}

sub search {
    my ($self, %opts) = @_;

    ($opts{sql}, $opts{bind}) = <: $dist.module :>::Util::bind_named($opts{sql}, $opts{bind} || {});

    $self->search_nonamed(%opts);
}

sub search_nonamed {
    my ($self, %opts) = @_;
    $opts{class}->require or die $@ if $opts{class};

    my $sql  = $opts{sql};
    my $bind = $opts{bind};

    my $res = $self->dbh($opts{db})->selectall_arrayref($sql, { Slice => {} }, @$bind);
    if ($opts{class}) {
        [ map { $opts{class}->new($_) } @$res ]
    } else {
        $res;
    }
}

sub query {
    my ($self, %opts) = @_;

    ($opts{sql}, $opts{bind}) = <: $dist.module :>::Util::bind_named($opts{sql}, $opts{bind} || {});

    $self->query_nonamed(%opts);
}

sub query_nonamed {
    my ($self, %opts) = @_;

    my $sql  = $opts{sql};
    my $bind = $opts{bind};

    $self->dbh($opts{db})->prepare_cached($sql)->execute(@$bind);
}

1;

