@@ script/mysqldiff
#!/usr/bin/env perl
use strict;
use warnings;

# TODO
# - drop key

@ARGV == 2 or die 'Usage: mysqldiff dbname1 dbname2';

my @tables = map {
    my $scheme;
    if (-e $_) {
        # file
        my $create_table = slurp($_);
        my $tmp_dbname = join '_', 'tmp', time();
        system(qw/mysqladmin -uroot create/, $tmp_dbname);
        system('mysql', '-uroot', $tmp_dbname, '-e', $create_table);
        my @cmd = (qw/mysqldump -uroot --no-data=true/, $tmp_dbname);
        $scheme = `@cmd`;
        system(qw/mysql -uroot/, $tmp_dbname, '-e', "drop database $tmp_dbname");
    } elsif (/ /) {
        # remote db
        my @cmd = (qw/mysqldump --no-data=true/, split /\s+/, $_);
        $scheme = `@cmd`;
        my $tmp_dbname = join '_', 'tmp', time();
        system(qw/mysqladmin -uroot create/, $tmp_dbname);
        system('mysql', '-uroot', $tmp_dbname, '-e', $scheme);
        @cmd = (qw/mysqldump -uroot --no-data=true/, $tmp_dbname);
        $scheme = `@cmd`;
        system(qw/mysql -uroot/, $tmp_dbname, '-e', "drop database $tmp_dbname");
    } else {
        # local db
        my $dbname = $_;
        my @cmd = (qw/mysqldump -uroot --no-data=true/, $dbname);
        $scheme = `@cmd`;
    }
    parse($scheme);
} @ARGV;

print diff(@tables);

sub slurp {
    my $filename = shift;
    open my $fh, '<', $filename or die;
    my $res = do { local $/; <$fh> };
    close $fh;
    $res;
}

sub parse {
    my $tables = [];
    my $scheme = shift or return $tables;
    for ($scheme =~ /(CREATE TABLE .*? ENGINE[^;]*);/smg) {
        my $content = $_;
        $content =~ /`(.*?)`/ or next;
        my $table_name = $1;
        my ($columns, $unique_keys,  $keys, $primary_keys);
        for my $line (split /\n/, $content) {
            $line =~ /^CREATE/ and next;
            $line =~ /^\)/ and next;
            if ($line =~ /^\s*PRIMARY KEY\s+\((.*)\)/) {
                push @$primary_keys, $1;
            } elsif ($line =~ /^\s*UNIQUE KEY.*?\((.*)\)/) {
                push @$unique_keys, $1;
            } elsif ($line =~ /^\s*KEY.*?\((.*)\)/) {
                push @$keys, $1;
            } elsif ($line =~ /^\s*`(.*?)`\s+(.+?)[\n,]?$/) {
                push @$columns, {
                    column     => $1,
                    definition => $2,
                }
            }
        }
        push @$tables, {
            table_name   => $table_name,
            primary_keys => $primary_keys || [],
            unique_keys  => $unique_keys || [],
            keys         => $keys || [],
            columns      => $columns || [],
            content      => $content,
        };
    }
    return $tables;
}

sub diff {
    my ($old, $new) = @_;
    my $diff = '';
    my @old_table_names = sort map { $_->{table_name} } @$old;
    my @new_table_names = sort map { $_->{table_name} } @$new;
    my (%old_hash, %new_hash);
    for (@$old) {
        $old_hash{$_->{table_name}} = $_;
    }
    for (@$new) {
        $new_hash{$_->{table_name}} = $_;
    }
    for my $name (@new_table_names) {
        if ($old_hash{$name}) {
            my $old_hash = $old_hash{$name};
            my $new_hash = $new_hash{$name};
            $diff .= table_diff($name, $old_hash, $new_hash);
        } else {
            $diff .= $new_hash{$name}->{content} . ";\n\n";
        }
    }

    return $diff;

}

sub table_diff {
    my ($name, $old, $new) = @_;
    my @change = grep {$_} (
        column_diff($old, $new),
        key_diff($old, $new),
    );
    @change or return '';
    return sprintf(
        "ALTER TABLE `%s` %s;\n\n",
        $name,
        join(', ', @change),
    );
}

sub column_diff {
    my ($old, $new) = @_;
    my $old_columns = $old->{columns};
    my $new_columns = $new->{columns};
    my (@change, %old_hash, %new_hash, %all_hash);
    for (@$old_columns) {
        $old_hash{$_->{column}} = $_;
        $all_hash{$_->{column}} = $_;
    }
    for (@$new_columns) {
        $new_hash{$_->{column}} = $_;
        $all_hash{$_->{column}} = $_;
    }
    for my $column (sort keys %all_hash) {
        my $old_definition = $old_hash{$column}->{definition};
        my $new_definition = $new_hash{$column}->{definition};
        if (!$old_definition) {
            push @change, "ADD `$column` $new_definition";
        } elsif (!$new_definition) {
            push @change, "DROP `$column`";
        } elsif ($old_definition ne $new_definition) {
            push @change, "MODIFY `$column` $new_definition";
        }
    }
    return @change;
}

sub key_diff {
    my ($old, $new) = @_;

    my @change;
    {# key
        my $old_keys = $old->{keys};
        my $new_keys = $new->{keys};
        my (%old_hash, %new_hash);
        for (@$old_keys) {
            $old_hash{$_} = 1;
        }
        for (@$new_keys) {
            $new_hash{$_} = 1;
        }
        for my $key (@$new_keys) {
            $old_hash{$key} and next;
            my $name = join '_', map { s{[`()]}{}g; $_ } split /,/, $key;
            push @change, "ADD INDEX `$name` ($key)";
        }
    };

    {# unique_key
        my $old_keys = $old->{unique_keys};
        my $new_keys = $new->{unique_keys};
        my (%old_hash, %new_hash);
        for (@$old_keys) {
            $old_hash{$_} = 1;
        }
        for (@$new_keys) {
            $new_hash{$_} = 1;
        }
        for my $key (@$new_keys) {
            $old_hash{$key} and next;
            my $name = join '_', map { s{[`()]}{}g; $_ } split /,/, $key;
            push @change, "ADD UNIQUE INDEX `$name` ($key)";
        }
    };

    return @change;
}

__END__

=head1 NAME

mysqldiff - diff for mysql

=head1 SYNOPSIS

  mysqldiff dbname1 dbname2
  mysqldiff '-uroot -hlocalhost dbname1' '-uroot -hlocalhost dbname2'
  mysqldiff createtable1.sql createtable2.sql

=cut

@@ script/server.pl
#!/usr/bin/env perl
use strict;
use warnings;

use lib 'lib';

use Getopt::Long qw(:config no_ignore_case pass_through);
use Proclet;

GetOptions(
    \my %opts, qw(
        enable-kyt-prof
    )
);

$ENV{DEBUG} = 1 unless defined $ENV{DEBUG};
$ENV{PERL_LWP_SSL_VERIFY_HOSTNAME} = 0;
$ENV{<: $dist.join_with("_") | upper :>_ACCESS_LOG} = 'log/access_log';
$ENV{<: $dist.join_with("_") | upper :>_ERROR_LOG}  = 'log/error_log';

if ($opts{'enable-kyt-prof'}) {
    require Devel::KYTProf;
    Devel::KYTProf->namespace_regex(qr/^<: $dist.module :>?/);
}

our $runner = Proclet->new(color => 1);


$runner->service(
    code => sub {
        my $plack_runner = Plack::Runner::<: $dist.module :>->new;
        $plack_runner->parse_options(
            '--port' => 3000,
            '--app' => 'script/app.psgi',
            '--Reload' => join(',', glob 'lib modules/*/lib'),

            '--server' => 'Starlet',
            '--max-workers' => 10,

            @ARGV,
        );
        $ENV{RIDGE_ENV} = $plack_runner->{env}; # -E は RIDGE_ENV に伝播
        $plack_runner->{env}  = 'development'; # PLACK_ENV は development 固定 (このスクリプトは開発時にしか使わないので)

        my $options = +{ @{ $plack_runner->{options} } };
        $ENV{SERVER_PORT} = $options->{port};
        $plack_runner->run;
    },
    tag => 'web',
);

$runner->run;


package Plack::Runner::<: $dist.module :>;
use strict;
use warnings;
use parent 'Plack::Runner';
use Plack::Runner;
use Plack::Builder;

sub prepare_devel {
    my ($self, $app) = @_;

    $app = Plack::Runner::build {
        my $app = shift;

        builder {
            enable 'Lint';
            enable 'StackTrace';

            mount '/'   => $app;
        };
    } $app;

    push @{$self->{options}}, server_ready => sub {
        my($args) = @_;
        my $name  = $args->{server_software} || ref($args); # $args is $server
        my $host  = $args->{host} || 0;
        my $proto = $args->{proto} || 'http';
        print STDERR "$name: Accepting connections at $proto://$host:$args->{port}/\n";
    };

    $app;
}

@@ script/setup
#!/bin/sh

dbname=$1
if [ "${dbname}" = '' ] ; then
  dbname=<: $dbname :>
fi

if mysqladmin -uroot create $dbname > /dev/null 2>&1; then
    echo "Init databases ${dbname}"
    mysql -uroot $dbname < db/schema.sql
else
    echo "Update databases ${dbname}"
    perl script/mysqldiff $dbname db/schema.sql | tee /dev/fd/2 | mysql -uroot $dbname
fi

git submodule update --init

