package t::frame8test;
use v5.14;
use utf8;
use strict;
use warnings;
use lib lib => 't/lib';

BEGIN {
    unless ($ENV{PLACK_ENV}) {
        $ENV{PLACK_ENV} = 'test';
    }
    if ($ENV{PLACK_ENV} eq 'production') {
        die "Do not run a test script on production environment";
    }
}

use Exporter::Lite;
use Scope::Container;
use Test::More 0.98;

use <: $dist.module :>::Context;
use <: $dist.module :>::Model::Backend;
use <: $dist.module :>::Model::Rrdfile;

our @EXPORT = qw(
    context
    mech
    create_backend
    create_rrdfile
);


sub import {
    feature->import(':5.14');
    utf8->import;
    strict->import;
    warnings->import;

    my ($class, @opts) = @_;
    my ($pkg, $file) = caller;
    my $code = qq[
        package $pkg;
        use parent 'Test::Class';
        use Test::Deep qw(cmp_deeply);
        use Test::Differences;
        use Test::Fatal qw(lives_ok dies_ok exception);
        use Test::More;

        END {
            if (\$0 eq "$file") {
                $pkg->runtests;
            }
        }
    ];

    eval $code;
    die $@ if $@;

    our $ScopeContainer = start_scope_container;

    @_ = ($class, @opts);
    goto &Exporter::Lite::import;
}

sub context () {
    unless (scope_container 'context') {
        require <: $dist.module :>::Context;
        scope_container context => <: $dist.module :>::Context->new;
    }
    return scope_container 'context';
}

sub mech (;$%) {
    require <: $dist.module :>::Test::Mechanize;

    state $mech = <: $dist.module :>::Test::Mechanize->new;
    return $mech;
}

1;

@@ t/lib/<: $dist.path :>/Test/Mechanize.pm
package <: $dist.module :>::Test::Mechanize;
use utf8;
use strict;
use warnings;
use lib 'lib';
use parent qw(Test::WWW::Mechanize::PSGI);

BEGIN {
    $ENV{PLACK_ENV} = 'test';
}

use Scope::Container;
use Class::Accessor::Lite(
    ro => [ qw(context env) ],
);

use <: $dist.module :>;

sub new {
    my ($class, $user) = @_;

    my $self;
    my $app = sub {
        my $env = shift;
        $self->{env} = $env;

        my $scope = start_scope_container;
        my $res = <: $dist.module :>->handle_request($env);
        $self->{context} = scope_container('context');

        return $res;
    };

    $self = $class->SUPER::new(app => $app);
    return $self;
}

sub json {
    my $self = shift;

    require JSON::XS;
    return JSON::XS::decode_json($self->content);
}

1;

